import React from 'react';
import './signup.css';

const API_BASE_URL = "http://localhost:5000/auth"; // Define base API URL

const Signup = () => {
    const handleGoogleSignup = async (role) => {
        try {
            const response = await fetch(`${API_BASE_URL}/google?role=${role}`);
            const data = await response.json();

            if (data.url) {
                window.location.href = data.url; // Redirect to Google sign-in page
            } else {
                console.error("Failed to get auth URL");
            }
        } catch (error) {
            console.error("Google Signup Error:", error);
        }
    };

    return (
        <div className="signup-container">
            <div className="signup-card">
                <div className="signup-header">
                    <h2>Create an Account</h2>
                    <p>Get started with our platform</p>
                </div>

                <div className="social-logins">
                    <button 
                        className="google-button"
                        onClick={() => handleGoogleSignup('User')} // Sign in as a regular User
                    >
                        <div className="google-icon"></div>
                        <span>Sign in with Google</span>
                    </button>

                    <button 
                        className="google-button"
                        onClick={() => handleGoogleSignup('Guide')} // Sign in as a Guide
                    >
                        <div className="google-icon"></div>
                        <span>Guide Login with Google</span>
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Signup;
