import React from "react";
import "./signup.css";

const Signup = () => {
  const handleGoogleSignUp = async () => {
    try {
      // Changed to GET request to match backend route
      const response = await fetch("http://localhost:5000/auth/google?role=student", {
        method: "GET",
        credentials: "include",
      });

      const data = await response.json();
      if (response.ok) {
        window.location.href = data.url; // Redirects user to Google login
      } else {
        alert(`Error: ${data.error}`);
      }
    } catch (error) {
      console.error("Google Sign-in Error:", error);
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-card">
        <div className="signup-header">
          <h2>Create an Account</h2>
          <p>Get started with our platform</p>
        </div>

        <div className="social-logins">
          <button className="google-button" onClick={handleGoogleSignUp}>
            <div className="google-icon"></div>
            <span>Student Sign up with Google</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default Signup;
