import React, { useState } from 'react';
import { FcGoogle } from 'react-icons/fc';
import { useNavigate } from 'react-router-dom';

const Signup = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  
  // Trixie color palette (assuming from landing page)
  const colors = {
    primary: '#6366F1', // Indigo
    secondary: '#F97316', // Orange
    background: '#F9FAFB',
    text: '#1F2937',
    lightGray: '#E5E7EB',
    white: '#FFFFFF'
  };
  
  const handleSignup = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }
    // Implement your signup logic here
    console.log('Signing up with:', email, password);
    setError('');
  };
  
  const handleGoogleSignup = (isAdmin = false) => {
    // Implement Google signup logic here
    console.log('Signing up with Google', isAdmin ? 'as admin' : 'as regular user');
    // After successful signup, redirect to appropriate page
    // navigate(isAdmin ? '/admin/dashboard' : '/dashboard');
  };
  
  const handleForgotPassword = () => {
    // Implement forgot password logic here
    console.log('Forgot password for:', email);
    // navigate('/reset-password');
  };
  
  return (
    <div 
      style={{ 
        backgroundColor: colors.background,
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '20px'
      }}
    >
      <div 
        style={{ 
          backgroundColor: colors.white,
          borderRadius: '8px',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
          padding: '40px',
          width: '100%',
          maxWidth: '480px'
        }}
      >
        <div style={{ textAlign: 'center', marginBottom: '24px' }}>
          <h1 style={{ 
            fontSize: '24px', 
            fontWeight: 'bold', 
            color: colors.text,
            marginBottom: '16px'
          }}>
            Join Trixie
          </h1>
          <p style={{ color: colors.text, opacity: 0.8 }}>
            Sign up to start your journey with Trixie
          </p>
        </div>
        
        {/* Google Sign-up Buttons */}
        <div style={{ marginBottom: '24px', display: 'flex', flexDirection: 'column', gap: '12px' }}>
          <button 
            onClick={() => handleGoogleSignup(false)}
            style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '12px',
              padding: '12px',
              backgroundColor: colors.white,
              color: colors.text,
              border: `1px solid ${colors.lightGray}`,
              borderRadius: '6px',
              fontWeight: '500',
              cursor: 'pointer',
              transition: 'all 0.2s',
              width: '100%'
            }}
          >
            <FcGoogle size={20} />
            <span>Sign up with Google</span>
          </button>
          
          <button 
            onClick={() => handleGoogleSignup(true)}
            style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '12px',
              padding: '12px',
              backgroundColor: colors.text,
              color: colors.white,
              border: 'none',
              borderRadius: '6px',
              fontWeight: '500',
              cursor: 'pointer',
              transition: 'all 0.2s',
              width: '100%'
            }}
          >
            <FcGoogle size={20} />
            <span>Admin Sign up with Google</span>
          </button>
        </div>
        
        <div style={{ 
          display: 'flex', 
          alignItems: 'center', 
          margin: '24px 0',
          color: colors.text,
          opacity: 0.5
        }}>
          <div style={{ flex: 1, height: '1px', backgroundColor: colors.lightGray }}></div>
          <div style={{ margin: '0 12px' }}>or</div>
          <div style={{ flex: 1, height: '1px', backgroundColor: colors.lightGray }}></div>
        </div>
        
        {/* Email/Password Form */}
        <form onSubmit={handleSignup}>
          {error && (
            <div style={{ 
              backgroundColor: '#FEE2E2', 
              color: '#DC2626', 
              padding: '12px', 
              borderRadius: '4px',
              marginBottom: '16px' 
            }}>
              {error}
            </div>
          )}
          
          <div style={{ marginBottom: '16px' }}>
            <label 
              htmlFor="email" 
              style={{ 
                display: 'block', 
                marginBottom: '6px',
                color: colors.text,
                fontSize: '14px',
                fontWeight: '500'
              }}
            >
              Email
            </label>
            <input 
              id="email"
              type="email" 
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              style={{
                width: '100%',
                padding: '12px',
                border: `1px solid ${colors.lightGray}`,
                borderRadius: '6px',
                fontSize: '14px'
              }}
              placeholder="Enter your email"
            />
          </div>
          
          <div style={{ marginBottom: '16px' }}>
            <label 
              htmlFor="password" 
              style={{ 
                display: 'block', 
                marginBottom: '6px',
                color: colors.text,
                fontSize: '14px',
                fontWeight: '500'
              }}
            >
              Password
            </label>
            <input 
              id="password"
              type="password" 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              style={{
                width: '100%',
                padding: '12px',
                border: `1px solid ${colors.lightGray}`,
                borderRadius: '6px',
                fontSize: '14px'
              }}
              placeholder="Create a password"
            />
          </div>
          
          <div style={{ marginBottom: '16px' }}>
            <label 
              htmlFor="confirmPassword" 
              style={{ 
                display: 'block', 
                marginBottom: '6px',
                color: colors.text,
                fontSize: '14px',
                fontWeight: '500'
              }}
            >
              Confirm Password
            </label>
            <input 
              id="confirmPassword"
              type="password" 
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
              style={{
                width: '100%',
                padding: '12px',
                border: `1px solid ${colors.lightGray}`,
                borderRadius: '6px',
                fontSize: '14px'
              }}
              placeholder="Confirm your password"
            />
          </div>
          
          <button 
            type="submit"
            style={{
              width: '100%',
              padding: '12px',
              backgroundColor: colors.primary,
              color: colors.white,
              border: 'none',
              borderRadius: '6px',
              fontWeight: '500',
              cursor: 'pointer',
              marginTop: '8px',
              transition: 'all 0.2s'
            }}
          >
            Create Account
          </button>
        </form>
        
        {/* Forgot Password & Login */}
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between',
          alignItems: 'center',
          marginTop: '20px',
          fontSize: '14px'
        }}>
          <button 
            onClick={handleForgotPassword}
            style={{
              background: 'none',
              border: 'none',
              color: colors.primary,
              cursor: 'pointer',
              padding: '4px'
            }}
          >
            Forgot password?
          </button>
          
          <div>
            Already have an account?{' '}
            <button 
              onClick={() => navigate('/login')}
              style={{
                background: 'none',
                border: 'none',
                color: colors.primary,
                cursor: 'pointer',
                fontWeight: '500',
                padding: '4px'
              }}
            >
              Log in
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Signup;
