import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './signup.css';

const API_BASE_URL = "http://localhost:5000/auth";

const Signup = () => {
  const navigate = useNavigate();

  // Function to handle Google Signup
  const handleGoogleSignup = async (role) => {
    try {
      const response = await fetch(`${API_BASE_URL}/google?role=${role}`);
      const data = await response.json();

      if (data.url) {
        window.location.href = data.url; // Redirects to Google OAuth page
      } else {
        console.error("Failed to get auth URL");
      }
    } catch (error) {
      console.error("Google Signup Error:", error);
    }
  };

  // Callback Handling: Runs when user returns from Google Auth
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const accessToken = urlParams.get("access_token");

    if (accessToken) {
      fetch(`${API_BASE_URL}/callback?access_token=${accessToken}`)
        .then(response => response.json())
        .then(data => {
          if (data.user) {
            // Redirect based on role
            navigate(`/${data.user.role.toLowerCase()}`);
          } else {
            console.error("Authentication failed");
          }
        })
        .catch(error => console.error("Error during callback:", error));
    }
  }, [navigate]);

  return (
    <div className="signup-container">
      <div className="signup-card">
        <div className="signup-header">
          <h2>Create an Account</h2>
          <p>Get started with our platform</p>
        </div>

        <div className="social-logins">
          <button className="google-button" onClick={() => handleGoogleSignup("User")}>
            <div className="google-icon"></div>
            <span>Sign in with Google</span>
          </button>

          <button className="google-button" onClick={() => handleGoogleSignup("Guide")}>
            <div className="google-icon"></div>
            <span>Guide Login with Google</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default Signup;
