import React from "react";
import "./signup.css";

const Signup = () => {
  const handleGoogleSignup = async (isTeacher) => {
    try {
      const role = isTeacher ? "teacher" : "student";
      const response = await fetch("http://localhost:5000/auth/google", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ role }),
        credentials: "include", // Ensures cookies (if needed) are sent
      });

      const data = await response.json();
      if (response.ok) {
        alert(`Logged in as ${data.role}`);
        window.location.href = "/dashboard"; // Redirect to dashboard
      } else {
        alert(`Error: ${data.error}`);
      }
    } catch (error) {
      console.error("Google Sign-in Error:", error);
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-card">
        <div className="signup-header">
          <h2>Create an Account</h2>
          <p>Get started with our platform</p>
        </div>

        <div className="social-logins">
          <button className="google-button" >
            <div className="google-icon"></div>
            <span>Student Sign up with Google</span>
          </button>

          <button className="google-button" onClick={() => handleGoogleSignup()}>
            <div className="google-icon"></div>
            <span>Authority Login with Google</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default Signup;
